mapper.update:
UPDATE chat_groups
name=#{name}, owner_id=#{owner_id}, create_time=#{create_time}, last_active_time=#{last_active_time}, members=#{members},
WHERE id = #{id}

UPDATE chat_groups

        members = CONCAT(members, ';', #{username}),

    WHERE id = #{groupId}

UPDATE chat_groups

    members = REPLACE(members, CONCAT(';', #{username}), ''),

WHERE id = #{groupId}

mapper.select:
SELECT *
FROM chat_groups
WHERE 1=1

    AND id = #{id}


    AND name = #{name}


    AND owner_id = #{owner_id}


    AND binary members like concat('%', #{members}, '%')
