mapper.update:
UPDATE users
username = #{username}, password = #{password}, gender = #{gender}, age = #{age}, hometown = #{hometown}, major = #{major}, mbti = #{mbti}, avatar_url = #{avatar_url}, signature = #{signature}, telephone_number = #{telephone_number}, birthday = #{birthday}, mail = #{mail}, school = #{school},
WHERE id = #{id}

mapper.select:
SELECT *
FROM users
WHERE 1=1

    AND id = #{id}


    AND username = #{username}


    AND gender = #{gender}


    AND age = #{age}


    AND hometown = #{hometown}


    AND major = #{major}


    AND mbti = #{mbti}


    AND avatar_url = #{avatar_url}


    AND signature = #{signature}


    AND telephone_number = #{telephone_number}


    AND birthday = #{birthday}


    AND mail = #{mail}


    AND school = #{school}
